mapper.update:
update board
set count = count+1
where b_no = #{bno}

 
	update board 
	set isdelete='Y' 
	where b_no=#{bno}

 
	update board 
	set title=#{title}, content=#{content}
	where b_no=#{bno}

 
	update board 
	set like_count = like_count+1 
	where isdelete='N' and b_no=#{bno}

 
	update board 
	set like_count = like_count-1 
	where isdelete='N' and b_no=#{bno}

mapper.select:
 
	select count(*) 
	from b_like 
	where b_no=#{bno}

 
	select count(*) 
	from b_like 
	where b_no=#{bno} and m_no=#{mno}
