mapper.select:
select count(*)
from p_like
where p_no=#{pno}

select count(*)
from p_like
where p_no=#{pno} and m_no=#{mno}

mapper.update:
update posting
set like_count = like_count+1
where isdelete='N' and p_no=#{pno}

update posting
set like_count = like_count-1
where isdelete='N' and p_no=#{pno}

update p_reply
set isdelete='Y'
where prr_no=#{prno}

update p_reply
set content=#{content}
where pr_no=#{prno}
